import React, { useState } from "react";
import { auth, googleProvider, facebookProvider, appleProvider } from "../firebase";
import { signInWithEmailAndPassword, signInWithPopup } from "firebase/auth";
import { FaGoogle, FaFacebook, FaApple } from "react-icons/fa";
import { Link } from "react-router-dom";
import "../styles/Login.css";

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [rememberMe, setRememberMe] = useState(false);

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      await signInWithEmailAndPassword(auth, email, password);
      if (rememberMe) {
        localStorage.setItem("rememberMe", "true");
      }
      alert("Inicio de sesión exitoso");
    } catch (error) {
      alert("Error al iniciar sesión");
    }
  };

  const handleProviderLogin = async (provider) => {
    try {
      await signInWithPopup(auth, provider);
      alert("Inicio de sesión exitoso");
    } catch (error) {
      alert("Error al iniciar sesión");
    }
  };

  return (
    <div className="login-container">
      <div className="login-box">
        <h2>Iniciar Sesión</h2>
        <form onSubmit={handleLogin}>
          <input
            type="email"
            placeholder="Correo electrónico"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
          <input
            type="password"
            placeholder="Contraseña"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
          <div className="remember-me">
            <input
              type="checkbox"
              id="rememberMe"
              checked={rememberMe}
              onChange={(e) => setRememberMe(e.target.checked)}
            />
            <label htmlFor="rememberMe">Recordarme</label>
          </div>
          <button type="submit">Iniciar Sesión</button>
        </form>
        <div className="social-buttons">
          <button onClick={() => handleProviderLogin(googleProvider)} className="google-btn">
            <FaGoogle /> Google
          </button>
          <button onClick={() => handleProviderLogin(facebookProvider)} className="facebook-btn">
            <FaFacebook /> Facebook
          </button>
          <button onClick={() => handleProviderLogin(appleProvider)} className="apple-btn">
            <FaApple /> Apple
          </button>
        </div>
        <p>
          ¿No tienes una cuenta? <Link to="/register">Regístrate</Link>
        </p>
      </div>
    </div>
  );
};

export default Login;
