import React, { useState } from "react";
import { auth, googleProvider, facebookProvider, appleProvider } from "../firebase";
import { signInWithEmailAndPassword, signInWithPopup } from "firebase/auth";
import { FaGoogle, FaFacebook, FaApple } from "react-icons/fa";
import { Link } from "react-router-dom";
import "./Login.css";

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      await signInWithEmailAndPassword(auth, email, password);
      alert("Inicio de sesión exitoso");
    } catch (error) {
      alert(error.message);
    }
  };

  const handleGoogleLogin = async () => {
    try {
      await signInWithPopup(auth, googleProvider);
      alert("Inicio de sesión con Google exitoso");
    } catch (error) {
      alert(error.message);
    }
  };

  const handleFacebookLogin = async () => {
    try {
      await signInWithPopup(auth, facebookProvider);
      alert("Inicio de sesión con Facebook exitoso");
    } catch (error) {
      alert(error.message);
    }
  };

  const handleAppleLogin = async () => {
    try {
      await signInWithPopup(auth, appleProvider);
      alert("Inicio de sesión con Apple exitoso");
    } catch (error) {
      alert(error.message);
    }
  };

  return (
    <div className="login-container">
      {/* Sección Izquierda */}
      <div className="login-left">
        <img src="C:\Users\anton\Downloads\Vittence1.png"/>
      </div>

      {/* Sección Derecha */}
      <div className="login-right">
        <h2>Iniciar Sesión</h2>

        {/* Botones de Redes Sociales */}
        <div className="social-buttons">
          <button onClick={handleGoogleLogin} className="google-btn">
            <FaGoogle className="social-icon" /> Continuar con Google
          </button>
          <button onClick={handleFacebookLogin} className="facebook-btn">
            <FaFacebook className="social-icon" /> Continuar con Facebook
          </button>
          <button onClick={handleAppleLogin} className="apple-btn">
            <FaApple className="social-icon" /> Continuar con Apple
          </button>
        </div>

        <div className="separator">
          <span className="separator-text">- O -</span>
        </div>

        {/* Formulario */}
        <form onSubmit={handleLogin}>
          <input
            type="email"
            placeholder="Correo electrónico"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            className="form-input"
          />
          <input
            type="password"
            placeholder="Contraseña"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            className="form-input"
          />
          <button type="submit" className="submit-btn">Iniciar Sesión</button>
        </form>

        {/* Enlace de Registro */}
        <p className="auth-link">
          ¿No tienes una cuenta? <Link to="/register">Regístrate</Link>
        </p>
      </div>
    </div>
  );
};

export default Login;
