import React, { useState } from "react";
import { useAuth0 } from "@auth0/auth0-react";
import { FaGoogle } from "react-icons/fa";
import "./Login.css";

const Login = () => {
  const { loginWithPopup, user, isAuthenticated, logout } = useAuth0();
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const handlePopupLogin = async () => {
    try {
      await loginWithPopup();
    } catch (error) {
      alert(error.message);
    }
  };

  return (
    <div className="login-container">
      <h2>{isAuthenticated ? `Welcome, ${user.name}` : "Sign In"}</h2>
      {!isAuthenticated ? (
        <>
          <button onClick={handlePopupLogin} className="google-btn">
            <FaGoogle className="social-icon" /> Sign in with Google
          </button>
        </>
      ) : (
        <button onClick={() => logout({ returnTo: window.location.origin })}>
          Log Out
        </button>
      )}
    </div>
  );
};

export default Login;
