import React from "react";
import { useAuth0 } from "@auth0/auth0-react";
import { FaGoogle, FaFacebook, FaApple } from "react-icons/fa";
import { Link } from "react-router-dom";
import "./Login.css";

const Login = () => {
  const { loginWithRedirect, logout, user, isAuthenticated } = useAuth0();

  return (
    <div className="login-container">
      {/* Left Section */}
      <div className="login-left">
        <img src="/images/Vittence2.png" alt="Logo" />
      </div>

      {/* Right Section */}
      <div className="login-right">
        <h2>{isAuthenticated ? `Welcome, ${user.name}` : "Sign In"}</h2>

        {!isAuthenticated ? (
          <>
            {/* Social Media Buttons */}
            <div className="social-buttons">
              <button onClick={() => loginWithRedirect()} className="google-btn">
                <FaGoogle className="social-icon" /> Sign in with Auth0
              </button>
            </div>

            <div className="separator">
              <span className="separator-text">- OR -</span>
            </div>
          </>
        ) : null}

        {/* Logout */}
        {isAuthenticated && (
          <button onClick={() => logout({ logoutParams: { returnTo: window.location.origin } })} className="logout-btn">
            Log Out
          </button>
        )}

        {/* Register Link */}
        {!isAuthenticated && (
          <p className="auth-link">
            Don't have an account? <Link to="/register">Register</Link>
          </p>
        )}
      </div>
    </div>
  );
};

export default Login;
