import React, { useState } from "react";
import { auth, googleProvider, facebookProvider, appleProvider } from "../firebase";
import { signInWithEmailAndPassword, signInWithPopup } from "firebase/auth";
import { FaGoogle, FaFacebook, FaApple } from "react-icons/fa";
import { Link } from "react-router-dom";
import "./Login.css";

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [rememberMe, setRememberMe] = useState(false);

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      await signInWithEmailAndPassword(auth, email, password);
      if (rememberMe) {
        localStorage.setItem("rememberMe", "true");
      }
      alert("Inicio de sesión exitoso");
    } catch (error) {
      alert(error.message);
    }
  };

  const handleGoogleLogin = async () => {
    try {
      await signInWithPopup(auth, googleProvider);
      alert("Inicio de sesión con Google exitoso");
    } catch (error) {
      alert(error.message);
    }
  };

  const handleFacebookLogin = async () => {
    try {
      await signInWithPopup(auth, facebookProvider);
      alert("Inicio de sesión con Facebook exitoso");
    } catch (error) {
      alert(error.message);
    }
  };

  const handleAppleLogin = async () => {
    try {
      await signInWithPopup(auth, appleProvider);
      alert("Inicio de sesión con Apple exitoso");
    } catch (error) {
      alert(error.message);
    }
  };

  return (
    <div className="login">
      <h2>Iniciar Sesión</h2>
      <form onSubmit={handleLogin}>
        <input
          type="email"
          placeholder="Correo electrónico"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
        <input
          type="password"
          placeholder="Contraseña"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <div className="remember-me">
          <input
            type="checkbox"
            id="rememberMe"
            checked={rememberMe}
            onChange={(e) => setRememberMe(e.target.checked)}
          />
          <label htmlFor="rememberMe">Recordarme</label>
        </div>
        <button type="submit">Iniciar Sesión</button>
      </form>
      <div className="social-buttons">
        <button onClick={handleGoogleLogin} className="google-btn">
          <FaGoogle /> Iniciar con Google
        </button>
        <button onClick={handleFacebookLogin} className="facebook-btn">
          <FaFacebook /> Iniciar con Facebook
        </button>
        <button onClick={handleAppleLogin} className="apple-btn">
          <FaApple /> Iniciar con Apple
        </button>
      </div>
      <p>
        ¿No tienes una cuenta? <Link to="/register">Regístrate</Link>
      </p>
    </div>
  );
};

export default Login;
