import { useState } from "react";
import { FaGoogle, FaFacebook, FaApple } from "react-icons/fa";
import { auth, googleProvider } from "../firebaseConfig"; // Adjusted path to ensure correctness
import { signInWithEmailAndPassword, signInWithPopup } from "firebase/auth";
import { Link } from "react-router-dom";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Checkbox } from "@/components/ui/checkbox";

export default function AuthForm({ isLogin = true }) {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [rememberMe, setRememberMe] = useState(false);

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      await signInWithEmailAndPassword(auth, email, password);
      alert("Inicio de sesión exitoso");
    } catch (error) {
      console.error("Error de autenticación:", error);
      alert("Error al iniciar sesión. Verifique sus credenciales.");
    }
  };

  const handleGoogleLogin = async () => {
    try {
      await signInWithPopup(auth, googleProvider);
      alert("Inicio de sesión con Google exitoso");
    } catch (error) {
      console.error("Error de autenticación con Google:", error);
      alert("Error al iniciar sesión con Google.");
    }
  };

  return (
    <div className="flex justify-center items-center min-h-screen bg-gradient-to-br from-purple-500 to-blue-500">
      <Card className="w-full max-w-md p-6 shadow-lg bg-white rounded-2xl">
        <h2 className="text-2xl font-bold text-center mb-4">
          {isLogin ? "Iniciar Sesión" : "Regístrate"}
        </h2>
        <CardContent>
          <form onSubmit={handleLogin} className="flex flex-col gap-4">
            <Input type="email" placeholder="Correo electrónico" className="w-full" value={email} onChange={(e) => setEmail(e.target.value)} required />
            <Input type="password" placeholder="Contraseña" className="w-full" value={password} onChange={(e) => setPassword(e.target.value)} required />
            <div className="flex items-center gap-2">
              <Checkbox checked={rememberMe} onCheckedChange={() => setRememberMe(!rememberMe)} />
              <span>Recuérdame</span>
            </div>
            <Button type="submit" className="w-full bg-purple-600 hover:bg-purple-700 text-white">
              {isLogin ? "Iniciar Sesión" : "Registrarse"}
            </Button>
          </form>
          <div className="flex flex-col gap-2 mt-2">
            <Button onClick={handleGoogleLogin} className="w-full bg-red-600 hover:bg-red-700 text-white flex items-center justify-center gap-2">
              <FaGoogle /> Iniciar con Google
            </Button>
            <Button className="w-full bg-blue-600 hover:bg-blue-700 text-white flex items-center justify-center gap-2">
              <FaFacebook /> Iniciar con Facebook
            </Button>
            <Button className="w-full bg-black hover:bg-gray-900 text-white flex items-center justify-center gap-2">
              <FaApple /> Iniciar con Apple
            </Button>
          </div>
          <p className="text-center mt-4 text-sm">
            {isLogin ? "¿No tienes una cuenta?" : "¿Ya tienes una cuenta?"} {" "}
            <Link to={isLogin ? "/register" : "/login"} className="text-blue-500 hover:underline">
              {isLogin ? "Regístrate" : "Inicia Sesión"}
            </Link>
          </p>
        </CardContent>
      </Card>
    </div>
  );
}
